function getAllStructureCoordsFromLayout(layout, rcl) {
  if (!layout[rcl]) {
    return [];
  }

  let positionsByType = layout[rcl] && layout[rcl].buildings;
  let coords = [];

  for (let structureType in positionsByType) {
    coords = coords.concat(positionsByType[structureType].pos);
  }

  return _.unique(coords, coord => coord.x + 50 * coord.y);
}

let _allBunkerCoords = {};

for (let rcl of [1, 2, 3, 4, 5, 6, 7, 8]) {
  if (bunkerLayout[rcl].buildings) {
    _allBunkerCoords[rcl] = getAllStructureCoordsFromLayout(bunkerLayout, rcl);
  }

  if (rcl == 7 || rcl == 8) {
    // add center tile for advanced bunkers
    _allBunkerCoords[rcl].push(bunkerLayout.data.anchor);
  }
}

const allBunkerCoords = _allBunkerCoords;
const bunkerCoordLookup = _.mapValues(_allBunkerCoords, coordArr => _.zipObject(_.map(coordArr, c => [coordName(c), true]))); // Fast function for checking if a position is inside the bunker

/**
 @param {PathFinder.CostMatrix} foregroundPixels - object pixels. modified for output
 @param {number} oob - value used for pixels outside image bounds
 @return {PathFinder.CostMatrix}
 the oob parameter is used so that if an object pixel is at the image boundary
 you can avoid having that reduce the pixel's value in the final output. Set
 it to a high value (e.g., 255) for this. Set oob to 0 to treat out of bounds
 as background pixels.
 */
function applyDistanceTransform(foregroundPixels, oob = 255) {
  let dist = foregroundPixels; // not a copy. We're modifying the input
  // Variables to represent the 3x3 neighborhood of a pixel.

  let UL, U, UR;
  let L, mid, R;
  let BL, B, BR;
  let x, y, value;

  for (y = 0; y < 50; ++y) {
    for (x = 0; x < 50; ++x) {
      if (foregroundPixels.get(x, y) !== 0) {
        UL = dist.get(x - 1, y - 1);
        U = dist.get(x, y - 1);
        UR = dist.get(x + 1, y - 1);
        L = dist.get(x - 1, y);

        if (y == 0) {
          UL = oob;
          U = oob;
          UR = oob;
        }

        if (x == 0) {
          UL = oob;
          L = oob;
        }

        if (x == 49) {
          UR = oob;
        }

        dist.set(x, y, Math.min(UL, U, UR, L, 254) + 1);
      }
    }
  }

  for (y = 49; y >= 0; --y) {
    for (x = 49; x >= 0; --x) {
      mid = dist.get(x, y);
      R = dist.get(x + 1, y);
      BL = dist.get(x - 1, y + 1);
      B = dist.get(x, y + 1);
      BR = dist.get(x + 1, y + 1);

      if (y == 49) {
        BL = oob;
        B = oob;
        BR = oob;
      }

      if (x == 49) {
        R = oob;
        BR = oob;
      }

      if (x == 0) {
        BL = oob;
      }

      value = Math.min(mid, R + 1, BL + 1, B + 1, BR + 1);
      dist.set(x, y, value);
    }
  }

  return dist;
} // Compute a cost matrix for walkable pixels in a room


function walkablePixelsForRoom(roomName) {
  var costMatrix = new PathFinder.CostMatrix();
  const terrain = Game.map.getRoomTerrain(roomName);

  for (var y = 0; y < 50; ++y) {
    for (var x = 0; x < 50; ++x) {
      if (terrain.get(x, y) != TERRAIN_MASK_WALL) {
        costMatrix.set(x, y, 1);
      }
    }
  }

  return costMatrix;
}
function distanceTransform(roomName) {
  return applyDistanceTransform(walkablePixelsForRoom(roomName));
